mapper.update:
update boards 
set title = #{title},
	content = #{content}
where board_num = #{boardNum} and board_type = #{boardType}

update boards
set boards.delyn = 'Y'
where board_num = #{boardNum} and board_type = #{boardType}

update comments
set comments.delyn = 'Y'
where comment_num = #{commentNum}

update comments
set comments.content = #{content}
where comment_num = #{commentNum}
